class Breadcrumb{constructor(container,divider,cssClass){this.container=container;this.divider=divider;this.cssClass=cssClass}
extractHostname(url){var hostname;if(url.indexOf("://")>-1){hostname=url.split('/')[2]}
else{hostname=url.split('/')[0]}
hostname=hostname.split(':')[0];hostname=hostname.split('?')[0];return hostname}
extractPagename(url){var pagename;if(url.indexOf('/nl/')){pagename=url.substring(url.indexOf('/nl/')+4)}
return pagename}
init(){var URL=document.location.href
var hostname=this.extractHostname(URL);var page=this.extractPagename(URL);var list=document.createElement('ul');var listItem_hostname=document.createElement('li');var listItem_pagename=document.createElement('li');var listItem_divider=document.createElement('li');var link_hostname=document.createElement('a');var link_pagename=document.createElement('a');link_hostname.href='/';link_pagename.href='/nl/'+page;link_hostname.innerHTML='Home';link_pagename.innerHTML=page;listItem_hostname.appendChild(link_hostname);listItem_divider.innerHTML=this.divider;listItem_divider.style.marginLeft="15px";listItem_divider.style.marginRight="15px";listItem_pagename.appendChild(link_pagename);list.appendChild(listItem_hostname);list.appendChild(listItem_divider);list.appendChild(listItem_pagename);if(this.cssClass){list.classList.add(this.cssClass)}
var cont=document.getElementsByClassName(this.container)[0];cont.appendChild(list)}}
